mapper.update:
update darts_club_info 

resum = resum + #{resum} 
, incomes = incomes + #{incomes}


	cno = #{cno} 
	and version = #{version}

update darts_club_info 

resum = resum + #{resum} 


	cno = #{cno} 
	and version = #{version}

mapper.select:
select dci.cno ,dci.cname ,dci.describe_message ,dcp.province, dai.agname,sum(cost) cost from darts_club_info dci INNER JOIN darts_club_place dcp ON dci.cno = dcp.cno LEFT JOIN darts_agent_info dai ON dci.agno = dai.agno LEFT JOIN darts_game_order dgo ON dci.cno = dgo.auth_no and status = 4
AND dci.isvalid = 1
AND dci.cno like concat('%', #{cno}, '%')
AND dci.agno like concat('%', #{agno}, '%')
AND dci.describe_message like concat('%', #{describe_message}, '%')
AND order_time >= #{time_start}
AND order_time <= #{time_end}
GROUP BY dci.cno
ORDER BY ${orderSql}
