mapper.select:
        SELECT
			dc.id,dc.lft_pay_id,dc.amount,dc.agno,dc.ag_amount,dc.cno,
			dc.c_amount,DATE_FORMAT(dc.pay_time,'%Y-%m-%d %H:%i:%s') as pay_time,dc.remark 
		FROM
			darts_commission AS dc 
		WHERE
			1 = 1 


            and dc.cno = #{cno}



            and dc.pay_time = #{pay_time}



            and dc.user_id = #{user_id}


            ORDER BY dc.${orderSql}

SELECT ( SELECT count(1) FROM (SELECT count(1) cou FROM darts_commission WHERE pay_time>= DATE_SUB(CURDATE(), INTERVAL 30 DAY)
and cno = #{cno}
and agno = #{agno}
GROUP BY user_id ) AS a WHERE a.cou > 1 ) / ( SELECT count(1) FROM (SELECT count(*) nt FROM darts_commission WHERE pay_time>= DATE_SUB(CURDATE(), INTERVAL 30 DAY)
and cno = #{cno}
and agno = #{agno}
GROUP BY user_id ) AS b WHERE b.nt > 0 ) as probability

select (@i:=@i+1) as row_num,info.* from ( SELECT cl.cname, cl.cno, pl.city, '俱乐部' as type, ( SELECT count(*) FROM darts_equ_auth au WHERE au.auth_no = t.cno ) as eq_num, sum(t.c_amount) as month_amount, sum(t.amount) as month_pay_amount FROM darts_club_info cl LEFT JOIN darts_commission t ON t.cno = cl.cno LEFT JOIN darts_club_place pl ON t.cno = pl.cno where cl.isvalid = 1
and DATE_FORMAT(t.pay_time, '%Y-%m') = #{query_month}
GROUP BY t.cno order by cl.cno) info ,(select @i:=0) as it

SELECT (@i :=@i + 1) AS row_num, info.* FROM ( SELECT ( SELECT sum(t.c_amount) as c_amount_sum FROM darts_commission t WHERE DATE_FORMAT(t.pay_time, '%Y-%m-%d') = #{pay_time} and t.cno=cl.cno ) AS day_amount, ( SELECT sum(t.amount) as amount_sum FROM darts_commission t WHERE DATE_FORMAT(t.pay_time, '%Y-%m-%d') = #{pay_time} and t.cno=cl.cno ) AS amount_sum FROM darts_club_info cl LEFT JOIN darts_commission t ON t.cno = cl.cno LEFT JOIN darts_club_place pl ON t.cno = pl.cno WHERE cl.isvalid = 1
and DATE_FORMAT(t.pay_time, '%Y-%m') = #{query_month}
GROUP BY t.cno ORDER BY cl.cno ) info, (SELECT @i := 0) AS it

SELECT (@i :=@i + 1) AS row_num, info.* FROM ( SELECT ( SELECT sum(t.amount) FROM darts_commission t WHERE DATE_FORMAT(t.pay_time, '%Y-%m-%d') = #{pay_time} and t.cno=cl.cno ) AS day_amount FROM darts_club_info cl LEFT JOIN darts_commission t ON t.cno = cl.cno LEFT JOIN darts_club_place pl ON t.cno = pl.cno WHERE cl.isvalid = 1
and DATE_FORMAT(t.pay_time, '%Y-%m') = #{query_month}
GROUP BY5 t.cno ORDER BY cl.cno ) info, (SELECT @i := 0) AS it
