mapper.select:
SELECT dup.`id`, dup.`user_id`, dup.`order_id`, dup.`points`,DATE_FORMAT(dup.`log_time`,'%Y-%m-%d %H:%i:%s') as log_time , dup.`remark` FROM `darts_user_points` as dup WHERE 1=1
AND dup.user_id = #{user_id}
ORDER BY dup.log_time desc

SELECT rk.* FROM ( SELECT t.*,@rank := @rank + 1 AS rank FROM ( SELECT sum(d.points) AS points, d.user_id, d.game_type, u.nickname, u.headimgurl FROM darts_user_points d LEFT JOIN wt_user u ON d.user_id = u.uuid WHERE DATE_FORMAT( log_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
AND u.country = #{country}
AND u.province like concat('%', #{province}, '%')
AND d.game_type = #{game_type}
GROUP BY d.user_id
,d.game_type
ORDER BY points DESC ) AS t, (SELECT @rank := 0) r ) AS rk WHERE 1 = 1
AND rk.user_id = #{uuid}
